###
# transition/transversion ratio R(s1,s2) is the ratio of the total number of 
# transitions to the total number of transversions, where symbol substitutions 
# are inferred from mismatched corresponding symbols as when calculating Hamming distance
###

def transitionTransversionRatio(seq1,seq2):
	trans=0.0
	tranv=0.0
	for i in range (0,len(seq1)):
		if seq1[i]!=seq2[i] : 
			# trasition
			if ((seq1[i] == 'A') and (seq2[i]=='G')  or (seq1[i] == 'C') and (seq2[i]=='T') or
				(seq1[i] == 'G') and (seq2[i]=='A')  or (seq1[i] == 'T') and (seq2[i]=='C')):
				trans= trans+1 
			else: 
				tranv= tranv+1
	result = trans/tranv
	#print "Trans ",trans," Tranversation ",tranv
	return result


